import { StatusBar } from 'expo-status-bar';
import React,{useEffect, useState} from 'react';
import { StyleSheet, Text, View,Platform,KeyboardAvoidingView,SafeAreaView } from 'react-native';

import {GiftedChat} from "react-native-gifted-chat";
import Fire from "../../Fire";
const Chat=(props) =>{

    console.log("Check params:",props.navigation.state.params)
    console.log("Fire uid:",Fire.uid);

    const [messages,setMessages]=useState([]);
    const [user,setUser]=useState(null);
    

    useEffect(()=>{
        if(user===null)
        {
            setUser({
                _id:Fire.uid,
                name:props.navigation.state.params.name

            })
        }
        Fire.get(message=>setMessages(GiftedChat.append(messages,message)));

        return ()=>{
            Fire.off();
        }
    })
    
    if(Platform.OS==="android")
    {
        return (
            
            <KeyboardAvoidingView style={{flex:1}} behavior="padding" keyboardVerticalOffset={30} enabled>
                {user!==null?
                    <GiftedChat messages={messages} onSend={Fire.send} user={user}/>
                    :
                    <View></View>

                }
            </KeyboardAvoidingView>
        )
    }
    return (
        <SafeAreaView style={{flex:1}}>
            {user!==null?
                <GiftedChat messages={messages} onSend={Fire.send} user={user}/>
                :
                <View></View>

            }        
        </SafeAreaView>
    );

   
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default Chat;
