import { StatusBar } from 'expo-status-bar';
import React,{useEffect, useState} from 'react';
import { StyleSheet, Text, View,Platform,KeyboardAvoidingView,SafeAreaView } from 'react-native';

import {GiftedChat} from "react-native-gifted-chat";
import Fire from "../../Fire";
const Chat=(props) =>{

    const [messages,setMessages]=useState([]);

    const getuser=()=>{
        return {
            _id:Fire.uid,
            name:props.navigation.params.name
        }
    }

    useEffect(()=>{
        Fire.get(message=>setMessages(GiftedChat.append(messages,message)));

        return ()=>{
            Fire.off();
        }
    })

    const chat=<GiftedChat messages={messages} onSend={Fire.send} user={getuser()}/>
    if(Platform.OS==="android")
    {
        return (
            <KeyboardAvoidingView style={{flex:1}} behavior="padding" keyboardVerticalOffset={30} enabled>
                {chat}
            </KeyboardAvoidingView>
        )
    }
    return (
        <SafeAreaView style={{flex:1}}>
            {chat}
        </SafeAreaView>
    );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});

export default Chat;
